import React from "react";
import {
    Card, Typography,
    Grid, CardContent,
    Button, Avatar
} from '@material-ui/core/';

import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';

class CardWish extends React.Component {
    render() {
        const { wish, idx } = this.props;   /* Recebendo os valores por propriedade  */
        return (
            <Grid item xs={12} md={12}>
                <Card className="mt-3">
                    <Grid container md={12} xs={12}>
                        <Grid md={2}>
                            <Avatar
                                src={wish.imagem}
                                className="avatar"
                                imgProps={{ width: "100%", height: "100%" }}
                            >
                            </Avatar>
                        </Grid>
                        <Grid md={8}>
                            <Typography variant="h6" component="h4">
                                {wish.nome} - R$ {wish.preco} 
                            </Typography>
                            <Typography variant="h6" component="h4">
                                {wish.prioridade}
                            </Typography>
                        </Grid>
                        <Grid md={1}>
                            <Button
                                variant="contained"
                                color="secondary"
                                onClick={() => this.props.editar(wish, idx)}
                            >
                                <EditIcon></EditIcon>
                         </Button>
                        </Grid>
                        <Grid md={1}>
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={() => this.props.remove(wish, idx)}
                            >
                                <DeleteIcon></DeleteIcon>
                        </Button>
                        </Grid>
                    </Grid>
                </Card>
            </Grid>
        );
    }
}

export default CardWish;
