import React from "react";
import {
    Card, Typography,
    Grid, CardContent,
    Button, Avatar
} from '@material-ui/core/';


class CardWish extends React.Component {
    render() {
        const { wish, idx } = this.props;   /* Recebendo os valores por propriedade  */
        return (
            <Grid item xs={12}>
                <Card className="mt-3">
                    <Grid container>
                        <Grid md={3}>
                            <Avatar
                                src={wish.imagem}
                                width="50px"
                                heigth="50px"
                                imgProps={{ width: "100%", height: "50px" }}
                            >
                            </Avatar>
                        </Grid>
                        <Grid md={8}>
                            {wish.nome}
                        </Grid>
                        <Grid md={2}>
                            <Button
                                variant="contained"
                                color="secondary"
                                onClick={() => this.props.editar(wish, idx)}
                            >
                                Editar
                         </Button>
                        </Grid>
                        <Grid md={2}>
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={() => this.props.remove(wish, idx)}
                            >
                                Remove
                        </Button>
                        </Grid>
                    </Grid>
                </Card>
            </Grid>
        );
    }
}

export default CardWish;
