import React from "react";
import {
    Card, Typography,
    Grid, CardContent,
    Button
} from '@material-ui/core/';


class CardWish extends React.Component {
    render() {
        const { wish, key } = this.props;   /* Recebendo os valores por propriedade  */
        return (
            <Grid item xs={12}>
                <Card className="mt-3">
                    <Grid container>
                        <Grid md={3}>
                            <Avatar
                                aria-label="tournament-logo"
                                alt="tournament-logo"
                                title={tournament.name}
                                src={RestAPI.getPhoto(tournament.logo)}
                            >
                                {tournament.name ? tournament.name.substr(0, 1) : "T"}
                            </Avatar>
                        </Grid>
                        <Grid md={8}>
                            {wish.nome}
                        </Grid>
                        <Grid md={2}>
                            <Button
                                variant="contained"
                                color="secondary"
                                onClick={() => this.editarItem(wish, key)}
                            >
                                Editar
                         </Button>
                        </Grid>
                        <Grid md={2}>
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={() => this.removeItem(wish, key)}
                            >
                                Remove
                        </Button>
                        </Grid>
                    </Grid>
                </Card>
            </Grid>
        );
    }
}

export default CardWish;
